mapper.insert:
insert into users
            (
            username
          , password
          , email
          , nickname
          , tel
            )
     values (
            #{username}
          , #{password}
          , #{email}
          , #{nickname, jdbcType=VARCHAR}
          , #{tel, jdbcType=VARCHAR}
            )

mapper.select:
update users
   set password     = #{password}
     , deleted      = #{deleted}
     , updated_date = current_timestamp
 where user_no = #{no}
